import React, { Component, Fragment } from 'react';
import '../Blogs/Blogs.css';
import BlogsComponents from '../../component/BlogsComponent/BlogsComponents';
// import axios
// import axos from 'axios';
import axios from 'axios';

export default class Blogs extends Component {
  //buat state utk looping dan tampung data drJSON API
  state = {
    post: []
  };

  getPostApi = () => {
    axios.get('http://localhost:3004/posts').then(result => {
      this.setState({
        post: result.data
      });
    });
  };

  handleRemove = data => {
    // console.log(data);
    //
    axios.delete(`http://localhost:3004/posts/${data}`).then(res => {
      // console.log(res);
      this.getPostApi();
    });
  };

  // memanggil data dari JSON API
  componentDidMount() {
    // fetch('https://jsonplaceholder.typicode.com/posts')
    //   .then(response => response.json())
    //   .then(json => console.log(json));
    // axios.get('http://localhost:3004/posts').then(result => {
    //   this.setState({
    //     post: result.data
    //   });
    // });
    this.getPostApi(); //
  }

  render() {
    return (
      <Fragment>
        <h2 className='title'>Welcome to Adi's Blogs</h2>
        {/* <BlogsComponents title='title' desc='Deskripsi' /> */}
        <div className='content'>
          <div className='formdata'>
            <label htmlfor='title' className='judul'>
              Title Blog
            </label>
            <input
              type='text'
              name='title'
              placeholder='add title'
              className='title2'
            />
            <label htmlFor='body-content' className='judul'>
              Blogs Content
            </label>
            <textarea
              name='body-content'
              id='ody-content'
              cols='40'
              rows='15'
              placeholder='add blo content'
              className='title2'
            ></textarea>
            <button className='btn-submit'>Simpan Data</button>
          </div>
          {this.state.post.map(post => {
            return (
              <BlogsComponents
                key={post.id}
                data={post}
                remove={this.handleRemove}
              />
            );
          })}
        </div>
      </Fragment>
    );
  }
}
